import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton,
  Alert
} from '@mui/material';
import { LocationOn, Refresh, MyLocation, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// Simplified zones - most commonly used
const eSolatZones = [
  // Wilayah Persekutuan
  { value: 'WLY01', label: 'Kuala Lumpur' },
  { value: 'WLY02', label: 'Putrajaya' },
  
  // Selangor
  { value: 'SGR01', label: 'Kajang, Ampang, Cheras, Petaling Jaya' },
  { value: 'SGR02', label: 'Shah Alam, Klang' },
  { value: 'SGR03', label: 'Kuala Selangor, Rawang' },
  
  // Johor
  { value: 'JHR01', label: 'Johor Bahru, Kulai' },
  { value: 'JHR02', label: 'Kota Tinggi, Mersing' },
  { value: 'JHR03', label: 'Batu Pahat, Muar' },
  { value: 'JHR04', label: 'Kluang, Pontian' },
  
  // Kedah
  { value: 'KDH01', label: 'Alor Setar, Jitra' },
  { value: 'KDH02', label: 'Sungai Petani' },
  { value: 'KDH04', label: 'Kulim' },
  
  // Kelantan
  { value: 'KTN01', label: 'Kota Bharu' },
  
  // Melaka
  { value: 'MLK01', label: 'Bandar Melaka' },
  
  // Negeri Sembilan
  { value: 'NSN01', label: 'Seremban, Port Dickson' },
  
  // Pahang
  { value: 'PHG02', label: 'Kuantan, Temerloh' },
  
  // Perak
  { value: 'PRK02', label: 'Ipoh, Batu Gajah' },
  { value: 'PRK06', label: 'Taiping, Bagan Serai' },
  
  // Perlis
  { value: 'PLS01', label: 'Kangar' },
  
  // Pulau Pinang
  { value: 'PNG01', label: 'George Town, Penang' },
  { value: 'PNG02', label: 'Butterworth, Bukit Mertajam' },
  
  // Sabah
  { value: 'SBH01', label: 'Sandakan' },
  { value: 'SBH02', label: 'Tawau' },
  { value: 'SBH04', label: 'Kota Kinabalu, Papar' },
  
  // Sarawak
  { value: 'SWK01', label: 'Kuching, Samarahan' },
  { value: 'SWK02', label: 'Sri Aman, Betong' },
  { value: 'SWK03', label: 'Sibu, Mukah' },
  { value: 'SWK04', label: 'Miri, Limbang' },
  
  // Terengganu
  { value: 'TRG01', label: 'Kuala Terengganu, Marang' },
  { value: 'TRG04', label: 'Dungun, Kemaman' },
  
  // Labuan
  { value: 'LBN01', label: 'Labuan' }
];

// Popular cities mapping
const popularCities = {
  'Kuala Lumpur': 'WLY01',
  'Putrajaya': 'WLY02',
  'Kajang': 'SGR01',
  'Shah Alam': 'SGR02',
  'Petaling Jaya': 'SGR01',
  'Klang': 'SGR02',
  'Johor Bahru': 'JHR01',
  'Penang': 'PNG01',
  'Ipoh': 'PRK02',
  'Kota Bharu': 'KTN01',
  'Kuala Terengganu': 'TRG01',
  'Kuantan': 'PHG02',
  'Malacca': 'MLK01',
  'Seremban': 'NSN01',
  'Alor Setar': 'KDH01',
  'Kota Kinabalu': 'SBH04',
  'Kuching': 'SWK01',
  'Miri': 'SWK04',
  'Sibu': 'SWK03',
  'Labuan': 'LBN01'
};

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedZone, setSelectedZone] = useState('WLY01');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // WORKING e-solat API endpoint
  const getPrayerTimesFromESolat = async (zoneCode) => {
    try {
      setError(null);
      console.log(`Fetching prayer times for zone: ${zoneCode}`);
      
      // WORKING API endpoint - using the public API
      const apiUrl = `https://waktu-solat-api.herokuapp.com/api/v1/prayer/${zoneCode}`;
      
      // Alternative backup API
      // const apiUrl = `https://api.azanpro.com/times/today.json?zone=${zoneCode}`;
      
      const response = await fetch(apiUrl);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('API Response:', data);
      
      // Handle different API response formats
      let times;
      if (data.data && data.data.today) {
        // Format 1: waktu-solat-api
        times = data.data.today;
      } else if (data.prayerTime && data.prayerTime.length > 0) {
        // Format 2: direct e-solat
        times = data.prayerTime[0];
      } else if (data.times) {
        // Format 3: alternative API
        times = data.times;
      } else {
        throw new Error('Unexpected API response format');
      }
      
      return {
        fajr: times.fajr || times.subuh,
        dhuhr: times.dhuhr || times.zohor,
        asr: times.asr,
        maghrib: times.maghrib,
        isha: times.isha,
        method: 'JAKIM e-solat.gov.my',
        zone: zoneCode,
        success: true
      };
      
    } catch (error) {
      console.error('API error:', error);
      
      // Fallback to direct e-solat API with different parameters
      try {
        console.log('Trying fallback API...');
        const fallbackUrl = `https://www.e-solat.gov.my/index.php?r=esolatApi/takwimsolat&period=today&zone=${zoneCode}`;
        const fallbackResponse = await fetch(fallbackUrl);
        const fallbackData = await fallbackResponse.json();
        
        if (fallbackData.prayerTime && fallbackData.prayerTime.length > 0) {
          const times = fallbackData.prayerTime[0];
          return {
            fajr: times.fajr,
            dhuhr: times.dhuhr,
            asr: times.asr,
            maghrib: times.maghrib,
            isha: times.isha,
            method: 'JAKIM e-solat.gov.my (Fallback)',
            zone: zoneCode,
            success: true
          };
        }
      } catch (fallbackError) {
        console.error('Fallback also failed:', fallbackError);
      }
      
      setError(`Failed to load prayer times. Please try another zone.`);
      throw error;
    }
  };

  const loadPrayerTimes = async (zoneCode = selectedZone) => {
    setLoading(true);
    try {
      const times = await getPrayerTimesFromESolat(zoneCode);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
      setSelectedZone(zoneCode);
      setError(null);
    } catch (error) {
      console.error('Error loading prayer times:', error);
      // Keep the error state from getPrayerTimesFromESolat
    } finally {
      setLoading(false);
    }
  };

  const handleZoneChange = async (zoneCode) => {
    setSelectedZone(zoneCode);
    await loadPrayerTimes(zoneCode);
  };

  const handlePopularCityChange = async (city) => {
    const zoneCode = popularCities[city];
    if (zoneCode) {
      await handleZoneChange(zoneCode);
    }
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      
      if (prayerTime > now) {
        return {
          name: prayer.name,
          time: prayer.time,
          timeObject: prayerTime
        };
      }
    }

    // If all prayers passed, return first prayer of next day
    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return {
      name: 'Fajr',
      time: prayerTimes.fajr,
      timeObject: tomorrow,
      isTomorrow: true
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    
    // Handle both "HH:MM" and "HH:MM AM/PM" formats
    let time = timeStr;
    let modifier = '';
    
    if (timeStr.includes(' ')) {
      [time, modifier] = timeStr.split(' ');
    }
    
    let [hours, minutes] = time.split(':');
    
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    
    // Convert to 24-hour format if needed
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô', type: 'isha' }
  ];

  if (loading && !prayerTimes) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              üïå Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
            </Typography>
          </Box>
          
          <Button
            size="small"
            startIcon={<Refresh />}
            onClick={() => loadPrayerTimes()}
            disabled={loading}
          >
            Refresh
          </Button>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* Quick City Selection */}
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Popular Cities</InputLabel>
          <Select
            value=""
            label="Popular Cities"
            onChange={(e) => handlePopularCityChange(e.target.value)}
            disabled={loading}
          >
            <MenuItem value="" disabled>Select a city</MenuItem>
            {Object.keys(popularCities).map(city => (
              <MenuItem key={city} value={city}>{city}</MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Zone Selection */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Zone</InputLabel>
          <Select
            value={selectedZone}
            label="Select Zone"
            onChange={(e) => handleZoneChange(e.target.value)}
            disabled={loading}
          >
            {eSolatZones.map(zone => (
              <MenuItem key={zone.value} value={zone.value}>
                {zone.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        {prayerTimes && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {prayers.map((prayer) => {
              const isCompleted = isPracticeCompleted(prayer.type);
              const isNextPrayer = nextPrayer?.name === prayer.name;
              
              return (
                <Box 
                  key={prayer.name}
                  sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    p: 2,
                    borderRadius: 2,
                    bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                    border: isNextPrayer ? '2px solid' : '1px solid',
                    borderColor: isNextPrayer ? 'primary.main' : 'divider',
                    opacity: isCompleted ? 0.8 : 1
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Typography variant="h5">{prayer.icon}</Typography>
                    <Box>
                      <Typography 
                        variant="h6"
                        sx={{ 
                          textDecoration: isCompleted ? 'line-through' : 'none',
                          color: isCompleted ? 'text.secondary' : 'text.primary'
                        }}
                      >
                        {prayer.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {prayer.time || '--:--'}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    {isNextPrayer && (
                      <Chip 
                        label="Next" 
                        color="primary" 
                        size="small"
                      />
                    )}
                    
                    <IconButton
                      onClick={() => handlePracticeToggle(prayer.type)}
                      color={isCompleted ? "success" : "default"}
                      sx={{
                        border: isCompleted ? '2px solid' : '1px solid',
                        borderColor: isCompleted ? 'success.main' : 'grey.400'
                      }}
                    >
                      {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                    </IconButton>
                  </Box>
                </Box>
              );
            })}
          </Box>
        )}

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Source: JAKIM Prayer Times ‚Ä¢ Zone: {selectedZone}
          </Typography>
          <br />
          {prayerTimes?.method && (
            <Typography variant="caption" color="text.secondary">
              Method: {prayerTimes.method}
            </Typography>
          )}
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
