  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton,
  Alert
} from '@mui/material';
import { Refresh, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// Import the calculation function as fallback
import { calculatePrayerTimes as calculateLocalPrayerTimes } from '../../utils/prayerCalculator';

// Simplified zones with coordinates for local calculation
const malaysianZones = [
  // Wilayah Persekutuan
  { value: 'WLY01', label: 'Kuala Lumpur', lat: 3.1390, lng: 101.6869 },
  { value: 'WLY02', label: 'Putrajaya', lat: 2.9264, lng: 101.6964 },
  
  // Selangor
  { value: 'SGR01', label: 'Kajang, Petaling Jaya', lat: 3.0497, lng: 101.5841 },
  { value: 'SGR02', label: 'Shah Alam, Klang', lat: 3.0733, lng: 101.5185 },
  { value: 'SGR03', label: 'Kuala Selangor, Rawang', lat: 3.3500, lng: 101.2500 },
  
  // Johor
  { value: 'JHR01', label: 'Johor Bahru', lat: 1.4927, lng: 103.7414 },
  { value: 'JHR02', label: 'Kota Tinggi, Mersing', lat: 1.7333, lng: 103.9000 },
  { value: 'JHR03', label: 'Batu Pahat, Muar', lat: 1.8500, lng: 102.9333 },
  { value: 'JHR04', label: 'Kluang, Pontian', lat: 2.0333, lng: 103.3167 },
  
  // Kedah
  { value: 'KDH01', label: 'Alor Setar', lat: 6.1164, lng: 100.3667 },
  { value: 'KDH02', label: 'Sungai Petani', lat: 5.6500, lng: 100.4833 },
  
  // Kelantan
  { value: 'KTN01', label: 'Kota Bharu', lat: 6.1254, lng: 102.2381 },
  
  // Melaka
  { value: 'MLK01', label: 'Bandar Melaka', lat: 2.1896, lng: 102.2501 },
  
  // Negeri Sembilan
  { value: 'NSN01', label: 'Seremban', lat: 2.7259, lng: 101.9378 },
  
  // Pahang
  { value: 'PHG02', label: 'Kuantan', lat: 3.8167, lng: 103.3333 },
  
  // Perak
  { value: 'PRK02', label: 'Ipoh', lat: 4.5975, lng: 101.0901 },
  { value: 'PRK06', label: 'Taiping', lat: 4.8500, lng: 100.7333 },
  
  // Perlis
  { value: 'PLS01', label: 'Kangar', lat: 6.4333, lng: 100.2000 },
  
  // Pulau Pinang
  { value: 'PNG01', label: 'George Town, Penang', lat: 5.4141, lng: 100.3288 },
  { value: 'PNG02', label: 'Butterworth', lat: 5.3991, lng: 100.3667 },
  
  // Sabah
  { value: 'SBH01', label: 'Sandakan', lat: 5.8388, lng: 118.1173 },
  { value: 'SBH02', label: 'Tawau', lat: 4.2497, lng: 117.8872 },
  { value: 'SBH04', label: 'Kota Kinabalu', lat: 5.9804, lng: 116.0735 },
  
  // Sarawak
  { value: 'SWK01', label: 'Kuching', lat: 1.5397, lng: 110.3542 },
  { value: 'SWK03', label: 'Sibu', lat: 2.2870, lng: 111.8307 },
  { value: 'SWK04', label: 'Miri', lat: 4.4143, lng: 114.0086 },
  
  // Terengganu
  { value: 'TRG01', label: 'Kuala Terengganu', lat: 5.3296, lng: 103.1370 },
  
  // Labuan
  { value: 'LBN01', label: 'Labuan', lat: 5.2833, lng: 115.2333 }
];

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedZone, setSelectedZone] = useState('WLY01');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [usingAPI, setUsingAPI] = useState(false);
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // Try to fetch from e-solat with CORS proxy
  const getPrayerTimesFromESolat = async (zoneCode) => {
    try {
      setError(null);
      console.log(`Trying to fetch from e-solat for zone: ${zoneCode}`);
      
      // Use CORS proxy to avoid CORS issues
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const targetUrl = `https://www.e-solat.gov.my/index.php?r=esolatApi/takwimsolat&period=today&zone=${zoneCode}`;
      
      const response = await fetch(proxyUrl + targetUrl, {
        headers: {
          'X-Requested-With': 'XMLHttpRequest'
        }
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('e-solat API response:', data);
      
      if (data.prayerTime && data.prayerTime.length > 0) {
        const times = data.prayerTime[0];
        setUsingAPI(true);
        return {
          fajr: times.fajr,
          dhuhr: times.dhuhr,
          asr: times.asr,
          maghrib: times.maghrib,
          isha: times.isha,
          method: 'JAKIM e-solat.gov.my (Live)',
          zone: zoneCode,
          success: true
        };
      }
      throw new Error('No prayer times data received');
    } catch (error) {
      console.error('e-solat API failed, using local calculation:', error);
      throw error;
    }
  };

  // Local calculation as fallback
  const getPrayerTimesLocal = (zoneCode) => {
    const zone = malaysianZones.find(z => z.value === zoneCode);
    if (!zone) {
      throw new Error(`Zone not found: ${zoneCode}`);
    }
    
    const times = calculateLocalPrayerTimes(zone.lat, zone.lng);
    setUsingAPI(false);
    return {
      ...times,
      method: 'JAKIM Calculation (Local)',
      zone: zoneCode,
      success: true
    };
  };

  const loadPrayerTimes = async (zoneCode = selectedZone) => {
    setLoading(true);
    try {
      // First try API
      let times;
      try {
        times = await getPrayerTimesFromESolat(zoneCode);
      } catch (apiError) {
        console.log('API failed, using local calculation');
        times = getPrayerTimesLocal(zoneCode);
      }
      
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
      setSelectedZone(zoneCode);
      setError(null);
      
    } catch (error) {
      console.error('All methods failed:', error);
      setError('Failed to load prayer times. Using default calculation.');
      
      // Final fallback - hardcoded KL times
      const fallbackTimes = getPrayerTimesLocal('WLY01');
      setPrayerTimes(fallbackTimes);
      setNextPrayer(getNextPrayer(fallbackTimes));
    } finally {
      setLoading(false);
    }
  };

  const handleZoneChange = async (zoneCode) => {
    setSelectedZone(zoneCode);
    await loadPrayerTimes(zoneCode);
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      
      if (prayerTime > now) {
        return {
          name: prayer.name,
          time: prayer.time,
          timeObject: prayerTime
        };
      }
    }

    // If all prayers passed, return first prayer of next day
    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return {
      name: 'Fajr',
      time: prayerTimes.fajr,
      timeObject: tomorrow,
      isTomorrow: true
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    
    let time = timeStr;
    let modifier = '';
    
    if (timeStr.includes(' ')) {
      [time, modifier] = timeStr.split(' ');
    }
    
    let [hours, minutes] = time.split(':');
    
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô', type: 'isha' }
  ];

  if (loading && !prayerTimes) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              üïå Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
            </Typography>
          </Box>
          
          <Button
            size="small"
            startIcon={<Refresh />}
            onClick={() => loadPrayerTimes()}
            disabled={loading}
          >
            Refresh
          </Button>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {error && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* Zone Selection */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Location</InputLabel>
          <Select
            value={selectedZone}
            label="Select Location"
            onChange={(e) => handleZoneChange(e.target.value)}
            disabled={loading}
          >
            {malaysianZones.map(zone => (
              <MenuItem key={zone.value} value={zone.value}>
                {zone.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Data Source Info */}
        {prayerTimes && (
          <Alert 
            severity={usingAPI ? "success" : "info"} 
            sx={{ mb: 2 }}
          >
            {usingAPI ? 
              "‚úÖ Using live data from e-solat.gov.my" : 
              "‚ÑπÔ∏è Using local calculation (API unavailable)"
            }
          </Alert>
        )}

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        {prayerTimes && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {prayers.map((prayer) => {
              const isCompleted = isPracticeCompleted(prayer.type);
              const isNextPrayer = nextPrayer?.name === prayer.name;
              
              return (
                <Box 
                  key={prayer.name}
                  sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    p: 2,
                    borderRadius: 2,
                    bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                    border: isNextPrayer ? '2px solid' : '1px solid',
                    borderColor: isNextPrayer ? 'primary.main' : 'divider',
                    opacity: isCompleted ? 0.8 : 1
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Typography variant="h5">{prayer.icon}</Typography>
                    <Box>
                      <Typography 
                        variant="h6"
                        sx={{ 
                          textDecoration: isCompleted ? 'line-through' : 'none',
                          color: isCompleted ? 'text.secondary' : 'text.primary'
                        }}
                      >
                        {prayer.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {prayer.time || '--:--'}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    {isNextPrayer && (
                      <Chip 
                        label="Next" 
                        color="primary" 
                        size="small"
                      />
                    )}
                    
                    <IconButton
                      onClick={() => handlePracticeToggle(prayer.type)}
                      color={isCompleted ? "success" : "default"}
                      sx={{
                        border: isCompleted ? '2px solid' : '1px solid',
                        borderColor: isCompleted ? 'success.main' : 'grey.400'
                      }}
                    >
                      {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                    </IconButton>
                  </Box>
                </Box>
              );
            })}
          </Box>
        )}

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            {prayerTimes?.method} ‚Ä¢ Zone: {selectedZone}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            {usingAPI ? 
              "Live data from JAKIM" : 
              "Calculated locally based on JAKIM method"
            }
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
