import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress
} from '@mui/material';
import { LocationOn, Refresh, MyLocation } from '@mui/icons-material';
import { calculatePrayerTimes, getCurrentLocation, getNextPrayer } from '../../utils/prayerTimes';

// Define cities directly in the component to avoid import issues
const malaysianCities = {
  // West Malaysia
  'Kuala Lumpur': { lat: 3.1390, lng: 101.6869 },
  'Putrajaya': { lat: 2.9264, lng: 101.6964 },
  'Johor Bahru': { lat: 1.4927, lng: 103.7414 },
  'Penang': { lat: 5.4141, lng: 100.3288 },
  'Ipoh': { lat: 4.5975, lng: 101.0901 },
  'Klang': { lat: 3.0449, lng: 101.4456 },
  'Kota Bharu': { lat: 6.1254, lng: 102.2381 },
  'Kuala Terengganu': { lat: 5.3296, lng: 103.1370 },
  'Kuantan': { lat: 3.8167, lng: 103.3333 },
  'Malacca': { lat: 2.1896, lng: 102.2501 },
  'Seremban': { lat: 2.7259, lng: 101.9378 },
  'Shah Alam': { lat: 3.0733, lng: 101.5185 },
  'Petaling Jaya': { lat: 3.1073, lng: 101.6067 },
  'Alor Setar': { lat: 6.1164, lng: 100.3667 },
  'Butterworth': { lat: 5.3991, lng: 100.3667 },
  'Taiping': { lat: 4.8500, lng: 100.7333 },
  
  // East Malaysia - Sabah
  'Kota Kinabalu': { lat: 5.9804, lng: 116.0735 },
  'Sandakan': { lat: 5.8388, lng: 118.1173 },
  'Tawau': { lat: 4.2497, lng: 117.8872 },
  'Lahad Datu': { lat: 5.0333, lng: 118.3167 },
  'Keningau': { lat: 5.3333, lng: 116.1667 },
  'Semporna': { lat: 4.4818, lng: 118.6112 },
  'Kudat': { lat: 6.8833, lng: 116.8333 },
  'Ranau': { lat: 5.9667, lng: 116.6833 },
  'Beaufort': { lat: 5.3473, lng: 115.7455 },
  
  // East Malaysia - Sarawak
  'Kuching': { lat: 1.5397, lng: 110.3542 },
  'Miri': { lat: 4.4143, lng: 114.0086 },
  'Sibu': { lat: 2.2870, lng: 111.8307 },
  'Bintulu': { lat: 3.1713, lng: 113.0415 },
  'Limbang': { lat: 4.7500, lng: 115.0000 },
  'Sarikei': { lat: 2.1167, lng: 111.5167 },
  'Sri Aman': { lat: 1.2372, lng: 111.4622 },
  'Kapit': { lat: 2.0167, lng: 112.9333 },
  'Mukah': { lat: 2.8944, lng: 112.0914 },
  
  // Federal Territory of Labuan
  'Labuan': { lat: 5.2833, lng: 115.2333 }
};

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedCity, setSelectedCity] = useState('Kuala Lumpur');
  const [loading, setLoading] = useState(true);
  const [userLocation, setUserLocation] = useState(null);

  const loadPrayerTimes = async (city = selectedCity) => {
    setLoading(true);
    try {
      let times;
      
      if (city === 'Current Location' && userLocation) {
        times = calculatePrayerTimes(userLocation.latitude, userLocation.longitude);
      } else {
        const cityData = malaysianCities[city];
        if (!cityData) {
          throw new Error(`City data not found for: ${city}`);
        }
        times = calculatePrayerTimes(cityData.lat, cityData.lng);
      }
      
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
    } catch (error) {
      console.error('Error loading prayer times:', error);
      // Fallback to default calculation
      const fallbackTimes = calculatePrayerTimes(3.1390, 101.6869); // KL coordinates
      setPrayerTimes(fallbackTimes);
      setNextPrayer(getNextPrayer(fallbackTimes));
    } finally {
      setLoading(false);
    }
  };

  const handleGetLocation = async () => {
    try {
      setLoading(true);
      const location = await getCurrentLocation();
      setUserLocation(location);
      setSelectedCity('Current Location');
      
      const times = calculatePrayerTimes(location.latitude, location.longitude);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
    } catch (error) {
      console.error('Location error:', error);
      alert('Unable to get your location. Using Kuala Lumpur instead.');
      setSelectedCity('Kuala Lumpur');
      loadPrayerTimes('Kuala Lumpur');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô' }
  ];

  if (loading) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Calculating prayer times...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            üïå Prayer Times
          </Typography>
          
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              size="small"
              startIcon={<MyLocation />}
              onClick={handleGetLocation}
              disabled={loading}
            >
              My Location
            </Button>
            <Button
              size="small"
              startIcon={<Refresh />}
              onClick={() => loadPrayerTimes()}
              disabled={loading}
            >
              Refresh
            </Button>
          </Box>
        </Box>

        {/* Location Selector */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Location</InputLabel>
          <Select
            value={selectedCity}
            label="Location"
            onChange={(e) => {
              setSelectedCity(e.target.value);
              loadPrayerTimes(e.target.value);
            }}
          >
            <MenuItem value="Current Location">üìç Current Location</MenuItem>
            {Object.keys(malaysianCities).map(city => (
              <MenuItem key={city} value={city}>{city}</MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          {prayers.map((prayer) => (
            <Box 
              key={prayer.name}
              sx={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                p: 2,
                borderRadius: 2,
                bgcolor: nextPrayer?.name === prayer.name ? 'action.hover' : 'transparent',
                border: nextPrayer?.name === prayer.name ? '2px solid' : '1px solid',
                borderColor: nextPrayer?.name === prayer.name ? 'primary.main' : 'divider'
              }}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Typography variant="h5">{prayer.icon}</Typography>
                <Box>
                  <Typography variant="h6">{prayer.name}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    {prayer.time || '--:--'}
                  </Typography>
                </Box>
              </Box>
              
              {nextPrayer?.name === prayer.name && (
                <Chip 
                  label="Next" 
                  color="primary" 
                  size="small"
                />
              )}
            </Box>
          ))}
        </Box>

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Method: {prayerTimes?.method}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Location: {selectedCity}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Calculated locally ‚Ä¢ 100% reliable
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
