import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton,
  Alert
} from '@mui/material';
import { Refresh, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// TODO: MANUALLY UPDATE THESE TIMES FROM e-solat.gov.my
// Visit: https://www.e-solat.gov.my/
// Select zone and copy EXACT times for today
const exactPrayerTimes = {
  // === COPY FROM e-solat.gov.my ===
  'WLY01': { // Kuala Lumpur
    fajr: '5:57 AM',     // ‚Üê COPY EXACT TIME
    sunrise: '7:01 AM',  // ‚Üê COPY EXACT TIME  
    dhuhr: '1:04 PM',    // ‚Üê COPY EXACT TIME
    asr: '4:17 PM',      // ‚Üê COPY EXACT TIME
    maghrib: '7:03 PM',  // ‚Üê COPY EXACT TIME
    isha: '8:16 PM'      // ‚Üê COPY EXACT TIME
  },
  'SGR01': { // Kajang
    fajr: '5:56 AM',     // ‚Üê COPY EXACT TIME
    sunrise: '7:00 AM',  // ‚Üê COPY EXACT TIME
    dhuhr: '1:03 PM',    // ‚Üê COPY EXACT TIME
    asr: '4:16 PM',      // ‚Üê COPY EXACT TIME
    maghrib: '7:02 PM',  // ‚Üê COPY EXACT TIME
    isha: '8:15 PM'      // ‚Üê COPY EXACT TIME
  },
  // Add more zones as needed...
};

// Malaysia zones
const malaysianZones = [
  { value: 'WLY01', label: 'Kuala Lumpur' },
  { value: 'SGR01', label: 'Kajang, Petaling' },
  { value: 'SGR02', label: 'Shah Alam, Klang' },
  { value: 'JHR01', label: 'Johor Bahru' },
  { value: 'PNG01', label: 'Penang' },
  { value: 'PRK02', label: 'Ipoh' },
  // Add more as needed...
];

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedZone, setSelectedZone] = useState('WLY01');
  const [loading, setLoading] = useState(false);
  const [needsUpdate, setNeedsUpdate] = useState(false);
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // Check if times need to be updated
  const checkIfUpdateNeeded = () => {
    const today = new Date();
    const lastUpdate = localStorage.getItem('prayerTimesLastUpdate');
    
    if (!lastUpdate) {
      setNeedsUpdate(true);
      return;
    }
    
    const lastUpdateDate = new Date(lastUpdate);
    const daysDiff = (today - lastUpdateDate) / (1000 * 60 * 60 * 24);
    
    if (daysDiff > 7) { // Update weekly
      setNeedsUpdate(true);
    }
  };

  const loadPrayerTimes = (zoneCode = selectedZone) => {
    setLoading(true);
    
    setTimeout(() => {
      const times = exactPrayerTimes[zoneCode];
      
      if (!times) {
        setPrayerTimes(null);
        setLoading(false);
        return;
      }
      
      setPrayerTimes({
        ...times,
        method: 'JAKIM e-solat.gov.my',
        zone: zoneCode,
        success: true
      });
      
      setNextPrayer(getNextPrayer(times));
      setSelectedZone(zoneCode);
      setLoading(false);
    }, 300);
  };

  const handleZoneChange = (zoneCode) => {
    setSelectedZone(zoneCode);
    loadPrayerTimes(zoneCode);
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      
      if (prayerTime > now) {
        return {
          name: prayer.name,
          time: prayer.time,
          timeObject: prayerTime
        };
      }
    }

    // If all prayers passed, return first prayer of next day
    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return {
      name: 'Fajr',
      time: prayerTimes.fajr,
      timeObject: tomorrow,
      isTomorrow: true
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    
    const [time, modifier] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  useEffect(() => {
    checkIfUpdateNeeded();
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô', type: 'isha' }
  ];

  if (loading && !prayerTimes) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              üïå Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
            </Typography>
          </Box>
          
          <Button
            size="small"
            startIcon={<Refresh />}
            onClick={() => loadPrayerTimes()}
            disabled={loading}
          >
            Refresh
          </Button>
        </Box>

        {needsUpdate && (
          <Alert severity="warning" sx={{ mb: 2 }}>
            <Typography variant="body2" fontWeight="bold">
              ‚ö†Ô∏è Update Required
            </Typography>
            <Typography variant="body2">
              Please update prayer times from e-solat.gov.my for accurate times
            </Typography>
          </Alert>
        )}

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {/* Zone Selection */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Location</InputLabel>
          <Select
            value={selectedZone}
            label="Select Location"
            onChange={(e) => handleZoneChange(e.target.value)}
            disabled={loading}
          >
            {malaysianZones.map(zone => (
              <MenuItem key={zone.value} value={zone.value}>
                {zone.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        {prayerTimes && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {prayers.map((prayer) => {
              const isCompleted = isPracticeCompleted(prayer.type);
              const isNextPrayer = nextPrayer?.name === prayer.name;
              
              return (
                <Box 
                  key={prayer.name}
                  sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    p: 2,
                    borderRadius: 2,
                    bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                    border: isNextPrayer ? '2px solid' : '1px solid',
                    borderColor: isNextPrayer ? 'primary.main' : 'divider',
                    opacity: isCompleted ? 0.8 : 1
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Typography variant="h5">{prayer.icon}</Typography>
                    <Box>
                      <Typography 
                        variant="h6"
                        sx={{ 
                          textDecoration: isCompleted ? 'line-through' : 'none',
                          color: isCompleted ? 'text.secondary' : 'text.primary'
                        }}
                      >
                        {prayer.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {prayer.time || '--:--'}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    {isNextPrayer && (
                      <Chip 
                        label="Next" 
                        color="primary" 
                        size="small"
                      />
                    )}
                    
                    <IconButton
                      onClick={() => handlePracticeToggle(prayer.type)}
                      color={isCompleted ? "success" : "default"}
                      sx={{
                        border: isCompleted ? '2px solid' : '1px solid',
                        borderColor: isCompleted ? 'success.main' : 'grey.400'
                      }}
                    >
                      {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                    </IconButton>
                  </Box>
                </Box>
              );
            })}
          </Box>
        )}

        {/* Update Instructions */}
        {needsUpdate && (
          <Alert severity="info" sx={{ mt: 2 }}>
            <Typography variant="body2" fontWeight="bold">
              üìã How to Update Times:
            </Typography>
            <Typography variant="body2">
              1. Visit <strong>e-solat.gov.my</strong>
            </Typography>
            <Typography variant="body2">
              2. Select your zone and copy exact times
            </Typography>
            <Typography variant="body2">
              3. Update the <code>exactPrayerTimes</code> object in this file
            </Typography>
          </Alert>
        )}

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            {prayerTimes?.method} ‚Ä¢ Zone: {selectedZone}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Manual update required for accuracy
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
