import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton,
  Alert
} from '@mui/material';
import { LocationOn, Refresh, MyLocation, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// EXACT same zones as e-solat.gov.my dropdown
const eSolatZones = [
  // Wilayah Persekutuan Kuala Lumpur
  { value: 'WLY01', label: 'Kuala Lumpur' },
  
  // Wilayah Persekutuan Putrajaya
  { value: 'WLY02', label: 'Putrajaya' },
  
  // Selangor
  { value: 'SGR01', label: 'Gombak, Petaling, Hulu Langat, Sepang (Kajang, Ampang, Cheras)' },
  { value: 'SGR02', label: 'Klang, Kuala Langat (Shah Alam, Klang)' },
  { value: 'SGR03', label: 'Kuala Selangor, Hulu Selangor, Sabak Bernam (Kuala Selangor, Rawang)' },
  
  // Johor
  { value: 'JHR01', label: 'Johor Bahru, Kulai (Johor Bahru, Kulai)' },
  { value: 'JHR02', label: 'Kota Tinggi, Mersing (Kota Tinggi, Mersing)' },
  { value: 'JHR03', label: 'Batu Pahat, Muar, Segamat, Tangkak (Batu Pahat, Muar)' },
  { value: 'JHR04', label: 'Kluang, Pontian (Kluang, Pontian)' },
  
  // Kedah
  { value: 'KDH01', label: 'Kota Setar, Pokok Sena, Kubang Pasu, Padang Terap (Alor Setar, Jitra)' },
  { value: 'KDH02', label: 'Kuala Muda, Yan, Pendang (Sungai Petani)' },
  { value: 'KDH03', label: 'Baling, Sik (Baling, Sik)' },
  { value: 'KDH04', label: 'Bandar Baharu, Kulim (Kulim)' },
  { value: 'KDH05', label: 'Langkawi (Langkawi)' },
  { value: 'KDH06', label: 'Pendang (Pendang)' },
  { value: 'KDH07', label: 'Pokok Sena (Pokok Sena)' },
  
  // Kelantan
  { value: 'KTN01', label: 'Bachok, Kota Bharu, Machang, Pasir Mas, Pasir Puteh, Tanah Merah, Tumpat, Kuala Krai (Kota Bharu)' },
  { value: 'KTN02', label: 'Gua Musang (Gua Musang)' },
  { value: 'KTN03', label: 'Jeli (Jeli)' },
  
  // Melaka
  { value: 'MLK01', label: 'Melaka Tengah, Alor Gajah, Jasin (Bandar Melaka)' },
  
  // Negeri Sembilan
  { value: 'NSN01', label: 'Seremban, Port Dickson, Kuala Pilah, Rembau (Seremban, Port Dickson)' },
  { value: 'NSN02', label: 'Jelebu, Jempol, Tampin (Jempol, Tampin)' },
  
  // Pahang
  { value: 'PHG01', label: 'Pekan, Rompin, Maran, Bera (Pekan)' },
  { value: 'PHG02', label: 'Kuantan, Temerloh, Jerantut (Kuantan, Temerloh)' },
  { value: 'PHG03', label: 'Lipis, Raub, Bentong (Raub, Bentong)' },
  { value: 'PHG04', label: 'Cameron Highlands, Genting Highlands, Bukit Tinggi (Cameron Highlands)' },
  
  // Perak
  { value: 'PRK01', label: 'Tapah, Slim River, Tanjung Malim (Tanjung Malim)' },
  { value: 'PRK02', label: 'Kuala Kangsar, Sg. Siput, Ipoh, Batu Gajah, Kampar (Ipoh, Batu Gajah)' },
  { value: 'PRK03', label: 'Lenggong, Pengkalan Hulu, Grik (Grik)' },
  { value: 'PRK04', label: 'Temengor, Belum (Temengor)' },
  { value: 'PRK05', label: 'Kg Gajah, Teluk Intan, Bagan Datuk, Seri Iskandar, Parit, Beruas, Lumut, Sitiawan, Pulau Pangkor (Teluk Intan, Lumut)' },
  { value: 'PRK06', label: 'Selama, Taiping, Bagan Serai, Parit Buntar (Taiping, Bagan Serai)' },
  { value: 'PRK07', label: 'Bukit Larut (Bukit Larut)' },
  
  // Perlis
  { value: 'PLS01', label: 'Perlis (Kangar)' },
  
  // Pulau Pinang
  { value: 'PNG01', label: 'Timur Laut, Barat Daya (George Town)' },
  { value: 'PNG02', label: 'Seberang Perai Utara, Seberang Perai Tengah, Seberang Perai Selatan (Butterworth, Bukit Mertajam)' },
  
  // Sabah
  { value: 'SBH01', label: 'Bahagian Sandakan (Timur) (Sandakan)' },
  { value: 'SBH02', label: 'Bahagian Tawau (Timur) (Tawau)' },
  { value: 'SBH03', label: 'Bahagian Kudat (Utara) (Kudat)' },
  { value: 'SBH04', label: 'Bahagian Pantai Barat (Barat) (Kota Kinabalu, Papar)' },
  { value: 'SBH05', label: 'Bahagian Pedalaman (Barat) (Beaufort, Keningau)' },
  { value: 'SBH06', label: 'Bahagian Sandakan (Timur) (Beluran)' },
  { value: 'SBH07', label: 'Bahagian Tawau (Timur) (Kunak)' },
  { value: 'SBH08', label: 'Bahagian Tawau (Timur) (Lahad Datu)' },
  { value: 'SBH09', label: 'Bahagian Sandakan (Timur) (Kinabatangan)' },
  { value: 'SBH10', label: 'Bahagian Pantai Barat (Barat) (Penampang)' },
  { value: 'SBH11', label: 'Bahagian Pantai Barat (Barat) (Putatan)' },
  { value: 'SBH12', label: 'Bahagian Pedalaman (Barat) (Tambunan)' },
  { value: 'SBH13', label: 'Bahagian Pedalaman (Barat) (Nabawan)' },
  { value: 'SBH14', label: 'Bahagian Pedalaman (Barat) (Sipitang)' },
  { value: 'SBH15', label: 'Bahagian Pedalaman (Barat) (Tenom)' },
  { value: 'SBH16', label: 'Bahagian Pantai Barat (Barat) (Kota Belud)' },
  { value: 'SBH17', label: 'Bahagian Pantai Barat (Barat) (Tuaran)' },
  { value: 'SBH18', label: 'Bahagian Kudat (Utara) (Kota Marudu)' },
  { value: 'SBH19', label: 'Bahagian Kudat (Utara) (Pitas)' },
  { value: 'SBH20', label: 'Bahagian Pedalaman (Barat) (Ranau)' },
  { value: 'SBH21', label: 'Bahagian Sandakan (Timur) (Telupid)' },
  { value: 'SBH22', label: 'Bahagian Tawau (Timur) (Semporna)' },
  { value: 'SBH23', label: 'Bahagian Pantai Barat (Barat) (Kuala Penyu)' },
  
  // Sarawak
  { value: 'SWK01', label: 'Kuching, Samarahan, Serian (Kuching, Samarahan)' },
  { value: 'SWK02', label: 'Sri Aman, Betong (Sri Aman, Betong)' },
  { value: 'SWK03', label: 'Sibu, Mukah, Kapit (Sibu, Mukah)' },
  { value: 'SWK04', label: 'Miri, Limbang, Bintulu (Miri, Limbang)' },
  { value: 'SWK05', label: 'Sarikei, Saratok (Sarikei)' },
  { value: 'SWK06', label: 'Kapit, Belaga, Song (Kapit)' },
  { value: 'SWK07', label: 'Limbang, Lawas (Limbang)' },
  { value: 'SWK08', label: 'Bintulu, Tatau, Sebauh (Bintulu)' },
  { value: 'SWK09', label: 'Miri, Marudi, Beluru, Telang Usan (Miri)' },
  
  // Terengganu
  { value: 'TRG01', label: 'Kuala Terengganu, Marang (Kuala Terengganu, Marang)' },
  { value: 'TRG02', label: 'Besut, Setiu (Besut, Setiu)' },
  { value: 'TRG03', label: 'Hulu Terengganu (Hulu Terengganu)' },
  { value: 'TRG04', label: 'Dungun, Kemaman (Dungun, Kemaman)' },
  
  // Labuan
  { value: 'LBN01', label: 'Labuan' }
];

// Popular cities mapping to help users
const popularCities = {
  'Kuala Lumpur': 'WLY01',
  'Putrajaya': 'WLY02',
  'Kajang': 'SGR01',
  'Shah Alam': 'SGR02',
  'Petaling Jaya': 'SGR01',
  'Klang': 'SGR02',
  'Johor Bahru': 'JHR01',
  'Penang': 'PNG01',
  'Ipoh': 'PRK02',
  'Kota Bharu': 'KTN01',
  'Kuala Terengganu': 'TRG01',
  'Kuantan': 'PHG02',
  'Malacca': 'MLK01',
  'Seremban': 'NSN01',
  'Alor Setar': 'KDH01',
  'Kota Kinabalu': 'SBH04',
  'Kuching': 'SWK01',
  'Miri': 'SWK04',
  'Sibu': 'SWK03',
  'Labuan': 'LBN01'
};

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedZone, setSelectedZone] = useState('WLY01');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // Get prayer times from e-solat API - EXACT same as website
  const getPrayerTimesFromESolat = async (zoneCode) => {
    try {
      setError(null);
      const today = new Date();
      const year = today.getFullYear();
      const month = (today.getMonth() + 1).toString().padStart(2, '0');
      const day = today.getDate().toString().padStart(2, '0');
      
      console.log(`Fetching from e-solat for zone: ${zoneCode}`);
      
      // EXACT same API endpoint as e-solat.gov.my
      const apiUrl = `https://www.e-solat.gov.my/index.php?r=esolatApi/takwimsolat&period=date&zone=${zoneCode}&date=${year}-${month}-${day}`;
      
      const response = await fetch(apiUrl);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      if (data.prayerTime && data.prayerTime.length > 0) {
        const times = data.prayerTime[0];
        console.log('Successfully fetched from e-solat:', times);
        
        return {
          fajr: times.fajr,
          dhuhr: times.dhuhr,
          asr: times.asr,
          maghrib: times.maghrib,
          isha: times.isha,
          method: 'JAKIM e-solat.gov.my',
          zone: zoneCode,
          date: times.date,
          success: true
        };
      }
      throw new Error('No prayer times data received from e-solat');
    } catch (error) {
      console.error('e-solat API error:', error);
      setError(`Failed to load prayer times: ${error.message}`);
      throw error;
    }
  };

  const loadPrayerTimes = async (zoneCode = selectedZone) => {
    setLoading(true);
    try {
      const times = await getPrayerTimesFromESolat(zoneCode);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
      setSelectedZone(zoneCode);
    } catch (error) {
      console.error('Error loading prayer times:', error);
      // Don't set fallback - let user choose another zone
    } finally {
      setLoading(false);
    }
  };

  const handleZoneChange = async (zoneCode) => {
    setSelectedZone(zoneCode);
    await loadPrayerTimes(zoneCode);
  };

  const handlePopularCityChange = async (city) => {
    const zoneCode = popularCities[city];
    if (zoneCode) {
      setSelectedZone(zoneCode);
      await loadPrayerTimes(zoneCode);
    }
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      
      if (prayerTime > now) {
        return {
          name: prayer.name,
          time: prayer.time,
          timeObject: prayerTime
        };
      }
    }

    // If all prayers passed, return first prayer of next day
    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return {
      name: 'Fajr',
      time: prayerTimes.fajr,
      timeObject: tomorrow,
      isTomorrow: true
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    
    const [time, modifier] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô', type: 'isha' }
  ];

  if (loading) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times from e-solat.gov.my...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              üïå Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
            </Typography>
          </Box>
          
          <Button
            size="small"
            startIcon={<Refresh />}
            onClick={() => loadPrayerTimes()}
            disabled={loading}
          >
            Refresh
          </Button>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* Quick City Selection */}
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Popular Cities</InputLabel>
          <Select
            value=""
            label="Popular Cities"
            onChange={(e) => handlePopularCityChange(e.target.value)}
          >
            <MenuItem value="" disabled>Select a city</MenuItem>
            {Object.keys(popularCities).map(city => (
              <MenuItem key={city} value={city}>{city}</MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Exact e-solat Zone Selection */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Zone (e-solat.gov.my)</InputLabel>
          <Select
            value={selectedZone}
            label="Select Zone (e-solat.gov.my)"
            onChange={(e) => handleZoneChange(e.target.value)}
          >
            {eSolatZones.map(zone => (
              <MenuItem key={zone.value} value={zone.value}>
                {zone.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          {prayers.map((prayer) => {
            const isCompleted = isPracticeCompleted(prayer.type);
            const isNextPrayer = nextPrayer?.name === prayer.name;
            
            return (
              <Box 
                key={prayer.name}
                sx={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  p: 2,
                  borderRadius: 2,
                  bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                  border: isNextPrayer ? '2px solid' : '1px solid',
                  borderColor: isNextPrayer ? 'primary.main' : 'divider',
                  opacity: isCompleted ? 0.8 : 1
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Typography variant="h5">{prayer.icon}</Typography>
                  <Box>
                    <Typography 
                      variant="h6"
                      sx={{ 
                        textDecoration: isCompleted ? 'line-through' : 'none',
                        color: isCompleted ? 'text.secondary' : 'text.primary'
                      }}
                    >
                      {prayer.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {prayer.time || '--:--'}
                    </Typography>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {isNextPrayer && (
                    <Chip 
                      label="Next" 
                      color="primary" 
                      size="small"
                    />
                  )}
                  
                  <IconButton
                    onClick={() => handlePracticeToggle(prayer.type)}
                    color={isCompleted ? "success" : "default"}
                    sx={{
                      border: isCompleted ? '2px solid' : '1px solid',
                      borderColor: isCompleted ? 'success.main' : 'grey.400'
                    }}
                  >
                    {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                  </IconButton>
                </Box>
              </Box>
            );
          })}
        </Box>

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Source: e-solat.gov.my ‚Ä¢ Zone: {selectedZone}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Data fetched directly from JAKIM official API
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Tap circle to mark prayer as completed
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
