// Simple prayer time calculation without external dependencies
const calculatePrayerTimes = (latitude, longitude, date = new Date()) => {
  // Convert to radians
  const toRad = (deg) => deg * (Math.PI / 180);
  
  // Date calculations
  const dayOfYear = Math.floor((date - new Date(date.getFullYear(), 0, 0)) / 1000 / 60 / 60 / 24);
  const declination = 23.45 * Math.sin(toRad((284 + dayOfYear) * 0.9863));
  
  // Equation of time (simplified)
  const B = toRad((dayOfYear - 81) * 0.9863);
  const equationOfTime = 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);
  
  // Base calculations for Malaysia (JAKIM method)
  const fajrAngle = 20; // Standard for Malaysia
  const ishaAngle = 18; // Standard for Malaysia
  
  // Calculate prayer times (simplified for Malaysia timezone +8)
  const timezoneOffset = 8;
  const timeCorrection = timezoneOffset + (longitude / 15) - (equationOfTime / 60);
  
  // Approximate sunrise and sunset (simplified)
  const sunrise = 7.0 - timeCorrection;
  const sunset = 19.0 + timeCorrection;
  
  // Calculate prayer times (simplified for demonstration)
  // In production, you would use more accurate formulas
  const times = {
    fajr: adjustTime(5.5 + (latitude / 100), timeCorrection),
    sunrise: adjustTime(sunrise, timeCorrection),
    dhuhr: adjustTime(12.0 + timeCorrection, 0),
    asr: adjustTime(calculateAsrTime(sunrise, sunset), timeCorrection),
    maghrib: adjustTime(sunset + 0.1, timeCorrection),
    isha: adjustTime(sunset + 1.5, timeCorrection)
  };
  
  return {
    fajr: formatTime(times.fajr),
    sunrise: formatTime(times.sunrise),
    dhuhr: formatTime(times.dhuhr),
    asr: formatTime(times.asr),
    maghrib: formatTime(times.maghrib),
    isha: formatTime(times.isha),
    method: 'JAKIM Malaysia Calculation',
    success: true
  };
};

// Calculate Asr time (Shafii method - shadow = 1 object length)
const calculateAsrTime = (sunrise, sunset) => {
  const daylightDuration = sunset - sunrise;
  return sunrise + (daylightDuration * 0.6); // Approximate Asr time
};

// Adjust time for timezone and corrections
const adjustTime = (decimalHours, correction) => {
  let hours = decimalHours + correction;
  if (hours < 0) hours += 24;
  if (hours >= 24) hours -= 24;
  return hours;
};

// Format decimal hours to 12-hour time string
const formatTime = (decimalHours) => {
  let hours = Math.floor(decimalHours);
  let minutes = Math.round((decimalHours - hours) * 60);
  
  if (minutes >= 60) {
    hours += 1;
    minutes -= 60;
  }
  if (hours >= 24) hours -= 24;
  if (hours < 0) hours += 24;
  
  const period = hours >= 12 ? 'PM' : 'AM';
  const displayHours = hours % 12 || 12;
  
  return `${displayHours}:${minutes.toString().padStart(2, '0')} ${period}`;
};

