import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton
} from '@mui/material';
import { LocationOn, Refresh, MyLocation, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { calculatePrayerTimes, getCurrentLocation, getNextPrayer } from '../../utils/prayerTimes';
import { usePractice } from '../../context/PracticeContext';

// Define cities directly in the component to avoid import issues
const malaysianCities = {
  // West Malaysia
  'Kuala Lumpur': { lat: 3.1390, lng: 101.6869 },
  'Putrajaya': { lat: 2.9264, lng: 101.6964 },
  'Johor Bahru': { lat: 1.4927, lng: 103.7414 },
  'Penang': { lat: 5.4141, lng: 100.3288 },
