  'Ranau': { lat: 5.9667, lng: 116.6833 },
  'Beaufort': { lat: 5.3473, lng: 115.7455 },
  
  // East Malaysia - Sarawak
  'Kuching': { lat: 1.5397, lng: 110.3542 },
  'Miri': { lat: 4.4143, lng: 114.0086 },
  'Sibu': { lat: 2.2870, lng: 111.8307 },
  'Bintulu': { lat: 3.1713, lng: 113.0415 },
  'Limbang': { lat: 4.7500, lng: 115.0000 },
  'Sarikei': { lat: 2.1167, lng: 111.5167 },
  'Sri Aman': { lat: 1.2372, lng: 111.4622 },
  'Kapit': { lat: 2.0167, lng: 112.9333 },
  'Mukah': { lat: 2.8944, lng: 112.0914 },
  
  // Federal Territory of Labuan
  'Labuan': { lat: 5.2833, lng: 115.2333 }
};

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedCity, setSelectedCity] = useState('Kuala Lumpur');
  const [loading, setLoading] = useState(true);
  const [userLocation, setUserLocation] = useState(null);
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  const loadPrayerTimes = async (city = selectedCity) => {
    setLoading(true);
    try {
      let times;
      
      if (city === 'Current Location' && userLocation) {
        times = calculatePrayerTimes(userLocation.latitude, userLocation.longitude);
      } else {
        const cityData = malaysianCities[city];
        if (!cityData) {
          throw new Error(`City data not found for: ${city}`);
        }
        times = calculatePrayerTimes(cityData.lat, cityData.lng);
      }
      
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
    } catch (error) {
      console.error('Error loading prayer times:', error);
      // Fallback to default calculation
      const fallbackTimes = calculatePrayerTimes(3.1390, 101.6869); // KL coordinates
      setPrayerTimes(fallbackTimes);
      setNextPrayer(getNextPrayer(fallbackTimes));
    } finally {
      setLoading(false);
    }
  };

  const handleGetLocation = async () => {
    try {
      setLoading(true);
      const location = await getCurrentLocation();
      setUserLocation(location);
      setSelectedCity('Current Location');
      
      const times = calculatePrayerTimes(location.latitude, location.longitude);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
    } catch (error) {
      console.error('Location error:', error);
      alert('Unable to get your location. Using Kuala Lumpur instead.');
      setSelectedCity('Kuala Lumpur');
      loadPrayerTimes('Kuala Lumpur');
    } finally {
      setLoading(false);
    }
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: '🌅', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '☀️', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: '🌇', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: '🌆', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: '🌙', type: 'isha' }
  ];

  if (loading) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Calculating prayer times...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header with Progress */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              🕌 Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
            </Typography>
          </Box>
          
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              size="small"
              startIcon={<MyLocation />}
              onClick={handleGetLocation}
              disabled={loading}
            >
              My Location
            </Button>
            <Button
              size="small"
              startIcon={<Refresh />}
              onClick={() => loadPrayerTimes()}
              disabled={loading}
            >
              Refresh
            </Button>
          </Box>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {/* Location Selector */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Location</InputLabel>
          <Select
            value={selectedCity}
            label="Location"
            onChange={(e) => {
              setSelectedCity(e.target.value);
              loadPrayerTimes(e.target.value);
            }}
          >
            <MenuItem value="Current Location">📍 Current Location</MenuItem>
            {Object.keys(malaysianCities).map(city => (
              <MenuItem key={city} value={city}>{city}</MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              🎯 Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          {prayers.map((prayer) => {
            const isCompleted = isPracticeCompleted(prayer.type);
            const isNextPrayer = nextPrayer?.name === prayer.name;
            
            return (
              <Box 
                key={prayer.name}
                sx={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  p: 2,
                  borderRadius: 2,
                  bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                  border: isNextPrayer ? '2px solid' : '1px solid',
                  borderColor: isNextPrayer ? 'primary.main' : 'divider',
                  opacity: isCompleted ? 0.8 : 1
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Typography variant="h5">{prayer.icon}</Typography>
                  <Box>
                    <Typography 
                      variant="h6"
                      sx={{ 
                        textDecoration: isCompleted ? 'line-through' : 'none',
                        color: isCompleted ? 'text.secondary' : 'text.primary'
                      }}
                    >
                      {prayer.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {prayer.time || '--:--'}
                    </Typography>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {isNextPrayer && (
                    <Chip 
                      label="Next" 
                      color="primary" 
                      size="small"
                    />
                  )}
                  
                  <IconButton
                    onClick={() => handlePracticeToggle(prayer.type)}
                    color={isCompleted ? "success" : "default"}
                    sx={{
                      border: isCompleted ? '2px solid' : '1px solid',
                      borderColor: isCompleted ? 'success.main' : 'grey.400'
                    }}
                  >
                    {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                  </IconButton>
                </Box>
              </Box>
            );
          })}
        </Box>

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Method: {prayerTimes?.method}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Location: {selectedCity}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Tap circle to mark prayer as completed
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
