sourcesContent:
import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  DialogContentText
} from '@mui/material';
import { Refresh, CheckCircle, RadioButtonUnchecked, MyLocation, LocationOn } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// EXACT same zones as e-solat.gov.my with optgroups
const malaysianZones = [
  { group: 'Johor', zones: [
    { value: 'JHR01', label: 'JHR01 - Pulau Aur dan Pulau Pemanggil' },
    { value: 'JHR02', label: 'JHR02 - Johor Bahru, Kota Tinggi, Mersing, Kulai' },
    { value: 'JHR03', label: 'JHR03 - Kluang, Pontian' },
    { value: 'JHR04', label: 'JHR04 - Batu Pahat, Muar, Segamat, Gemas Johor, Tangkak' }
  ]},
  { group: 'Kedah', zones: [
    { value: 'KDH01', label: 'KDH01 - Kota Setar, Kubang Pasu, Pokok Sena (Daerah Kecil)' },
    { value: 'KDH02', label: 'KDH02 - Kuala Muda, Yan, Pendang' },
    { value: 'KDH03', label: 'KDH03 - Padang Terap, Sik' },
    { value: 'KDH04', label: 'KDH04 - Baling' },
    { value: 'KDH05', label: 'KDH05 - Bandar Baharu, Kulim' },
    { value: 'KDH06', label: 'KDH06 - Langkawi' },
    { value: 'KDH07', label: 'KDH07 - Puncak Gunung Jerai' }
  ]},
  { group: 'Kelantan', zones: [
    { value: 'KTN01', label: 'KTN01 - Bachok, Kota Bharu, Machang, Pasir Mas, Pasir Puteh, Tanah Merah, Tumpat, Kuala Krai, Mukim Chiku' },
    { value: 'KTN02', label: 'KTN02 - Gua Musang (Daerah Galas Dan Bertam), Jeli, Jajahan Kecil Lojing' }
  ]},
  { group: 'Melaka', zones: [
    { value: 'MLK01', label: 'MLK01 - SELURUH NEGERI MELAKA' }
  ]},
  { group: 'Negeri Sembilan', zones: [
    { value: 'NGS01', label: 'NGS01 - Tampin, Jempol' },
    { value: 'NGS02', label: 'NGS02 - Jelebu, Kuala Pilah, Rembau' },
    { value: 'NGS03', label: 'NGS03 - Port Dickson, Seremban' }
  ]},
  { group: 'Pahang', zones: [
    { value: 'PHG01', label: 'PHG01 - Pulau Tioman' },
    { value: 'PHG02', label: 'PHG02 - Kuantan, Pekan, Muadzam Shah' },
    { value: 'PHG03', label: 'PHG03 - Jerantut, Temerloh, Maran, Bera, Chenor, Jengka' },
    { value: 'PHG04', label: 'PHG04 - Bentong, Lipis, Raub' },
    { value: 'PHG05', label: 'PHG05 - Genting Sempah, Janda Baik, Bukit Tinggi' },
    { value: 'PHG06', label: 'PHG06 - Cameron Highlands, Genting Higlands, Bukit Fraser' },
    { value: 'PHG07', label: 'PHG07 - Zon Khas Daerah Rompin, (Mukim Rompin, Mukim Endau, Mukim Pontian)' }
  ]},
  { group: 'Perlis', zones: [
    { value: 'PLS01', label: 'PLS01 - Kangar, Padang Besar, Arau' }
  ]},
  { group: 'Pulau Pinang', zones: [
    { value: 'PNG01', label: 'PNG01 - Seluruh Negeri Pulau Pinang' }
  ]},
  { group: 'Perak', zones: [
    { value: 'PRK01', label: 'PRK01 - Tapah, Slim River, Tanjung Malim' },
    { value: 'PRK02', label: 'PRK02 - Kuala Kangsar, Sg. Siput , Ipoh, Batu Gajah, Kampar' },
    { value: 'PRK03', label: 'PRK03 - Lenggong, Pengkalan Hulu, Grik' },
    { value: 'PRK04', label: 'PRK04 - Temengor, Belum' },
    { value: 'PRK05', label: 'PRK05 - Kg Gajah, Teluk Intan, Bagan Datuk, Seri Iskandar, Beruas, Parit, Lumut, Sitiawan, Pulau Pangkor' },
    { value: 'PRK06', label: 'PRK06 - Selama, Taiping, Bagan Serai, Parit Buntar' },
    { value: 'PRK07', label: 'PRK07 - Bukit Larut' }
  ]},
  { group: 'Sabah', zones: [
    { value: 'SBH01', label: 'SBH01 - Bahagian Sandakan (Timur), Bukit Garam, Semawang, Temanggong, Tambisan, Bandar Sandakan, Sukau' },
    { value: 'SBH02', label: 'SBH02 - Beluran, Telupid, Pinangah, Terusan, Kuamut, Bahagian Sandakan (Barat)' },
    { value: 'SBH03', label: 'SBH03 - Lahad Datu, Silabukan, Kunak, Sahabat, Sempurna, Tungku, Bahagian Tawau (Timur)' },
    { value: 'SBH04', label: 'SBH04 - Bandar Tawau, Balong, Merotai, Kalabakan, Bahagian Tawau (Barat)' },
    { value: 'SBH05', label: 'SBH05 - Kudat, Kota Marudu, Pitas, Pulau Banggi, Bahagian Kudat' },
    { value: 'SBH06', label: 'SBH06 - Gunung Kinabalu' },
    { value: 'SBH07', label: 'SBH07 - Kota Kinabalu, Ranau, Kota Belud, Tuaran, Penampang, Papar, Putatan, Bahagian Pantai Barat' },
    { value: 'SBH08', label: 'SBH08 - Pensiangan, Keningau, Tambunan, Nabawan, Bahagian Pendalaman (Atas)' },
    { value: 'SBH09', label: 'SBH09 - Beaufort, Kuala Penyu, Sipitang, Tenom, Long Pasia, Membakut, Weston, Bahagian Pendalaman (Bawah)' }
  ]},
  { group: 'Selangor', zones: [
    { value: 'SGR01', label: 'SGR01 - Gombak, Petaling, Sepang, Hulu Langat, Hulu Selangor, S.Alam' },
    { value: 'SGR02', label: 'SGR02 - Kuala Selangor, Sabak Bernam' },
    { value: 'SGR03', label: 'SGR03 - Klang, Kuala Langat' }
  ]},
  { group: 'Sarawak', zones: [
    { value: 'SWK01', label: 'SWK01 - Limbang, Lawas, Sundar, Trusan' },
    { value: 'SWK02', label: 'SWK02 - Miri, Niah, Bekenu, Sibuti, Marudi' },
    { value: 'SWK03', label: 'SWK03 - Pandan, Belaga, Suai, Tatau, Sebauh, Bintulu' },
    { value: 'SWK04', label: 'SWK04 - Sibu, Mukah, Dalat, Song, Igan, Oya, Balingian, Kanowit, Kapit' },
    { value: 'SWK05', label: 'SWK05 - Sarikei, Matu, Julau, Rajang, Daro, Bintangor, Belawai' },
    { value: 'SWK06', label: 'SWK06 - Lubok Antu, Sri Aman, Roban, Debak, Kabong, Lingga, Engkelili, Betong, Spaoh, Pusa, Saratok' },
    { value: 'SWK07', label: 'SWK07 - Serian, Simunjan, Samarahan, Sebuyau, Meludam' },
    { value: 'SWK08', label: 'SWK08 - Kuching, Bau, Lundu, Sematan' },
    { value: 'SWK09', label: 'SWK09 - Zon Khas (Kampung Patarikan)' }
  ]},
  { group: 'Terengganu', zones: [
    { value: 'TRG01', label: 'TRG01 - Kuala Terengganu, Marang, Kuala Nerus' },
    { value: 'TRG02', label: 'TRG02 - Besut, Setiu' },
    { value: 'TRG03', label: 'TRG03 - Hulu Terengganu' },
    { value: 'TRG04', label: 'TRG04 - Dungun, Kemaman' }
  ]},
  { group: 'Wilayah Persekutuan', zones: [
    { value: 'WLY01', label: 'WLY01 - Kuala Lumpur, Putrajaya' },
    { value: 'WLY02', label: 'WLY02 - Labuan' }
  ]}
];

// More precise GPS to Zone mapping with exact coordinates for major areas
const preciseGPSMapping = [
  // Selangor - More precise boundaries
  { lat: 3.0497, lng: 101.5841, zone: 'SGR01', name: 'Kajang' },
  { lat: 3.0733, lng: 101.5185, zone: 'SGR01', name: 'Shah Alam' },
  { lat: 3.1073, lng: 101.6067, zone: 'SGR01', name: 'Petaling Jaya' },
  { lat: 3.0449, lng: 101.4456, zone: 'SGR03', name: 'Klang' },
  { lat: 3.3500, lng: 101.2500, zone: 'SGR02', name: 'Kuala Selangor' },
  
  // Wilayah Persekutuan
  { lat: 3.1390, lng: 101.6869, zone: 'WLY01', name: 'Kuala Lumpur' },
  { lat: 2.9264, lng: 101.6964, zone: 'WLY01', name: 'Putrajaya' },
  
  // Johor - Major cities
  { lat: 1.4927, lng: 103.7414, zone: 'JHR02', name: 'Johor Bahru' },
  { lat: 2.0333, lng: 103.3167, zone: 'JHR03', name: 'Kluang' },
  { lat: 1.8500, lng: 102.9333, zone: 'JHR04', name: 'Batu Pahat' },
];

// Function to calculate distance between two coordinates (Haversine formula)
const calculateDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // Earth radius in km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
    Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
};

// Find the closest zone based on precise coordinates
const findClosestZone = (userLat, userLng) => {
  let closestZone = 'WLY01'; // Default fallback
  let shortestDistance = Infinity;
  
  for (const location of preciseGPSMapping) {
    const distance = calculateDistance(userLat, userLng, location.lat, location.lng);
    if (distance < shortestDistance) {
      shortestDistance = distance;
      closestZone = location.zone;
    }
  }
  
  console.log(`Closest zone found: ${closestZone}, Distance: ${shortestDistance.toFixed(2)}km`);
  return closestZone;
};

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedZone, setSelectedZone] = useState('WLY01');
  const [loading, setLoading] = useState(true);
  const [locationLoading, setLocationLoading] = useState(false);
  const [error, setError] = useState(null);
  const [usingGPS, setUsingGPS] = useState(false);
  const [userLocation, setUserLocation] = useState(null);
  const [locationDialog, setLocationDialog] = useState(false);
  const [locationPermission, setLocationPermission] = useState('prompt');
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // Check location permission status on component mount
  useEffect(() => {
    checkLocationPermission();
    attemptAutoLocation();
    
    // Setup background location tracking for notifications
    setupBackgroundLocationTracking();
  }, []);

  // Setup background location tracking for push notifications
  const setupBackgroundLocationTracking = () => {
    if ('serviceWorker' in navigator && 'PeriodicSyncManager' in window) {
      // Register for periodic background sync (for push notifications)
      navigator.serviceWorker.ready.then(registration => {
        // This would be used for background prayer time updates
        console.log('Background sync available for prayer reminders');
      });
    }
  };

  // Check if location permission is already granted
  const checkLocationPermission = async () => {
    if (!navigator.permissions) {
      setLocationPermission('prompt');
      return;
    }

    try {
      const permission = await navigator.permissions.query({ name: 'geolocation' });
      setLocationPermission(permission.state);
      
      permission.onchange = () => {
        setLocationPermission(permission.state);
        if (permission.state === 'granted') {
          attemptAutoLocation();
        }
      };
    } catch (error) {
      console.log('Permission API not supported');
      setLocationPermission('prompt');
    }
  };

  // Auto-attempt location detection on app start
  const attemptAutoLocation = async () => {
    // Only auto-detect if permission was previously granted
    if (locationPermission === 'granted') {
      console.log('Auto-detecting location (permission previously granted)');
      await handleDetectLocation(true); // silent mode
    } else {
      // Check if we have a previously stored location
      const storedZone = localStorage.getItem('userPrayerZone');
      const storedLocation = localStorage.getItem('userLocation');
      
      if (storedZone && storedLocation) {
        setSelectedZone(storedZone);
        setUsingGPS(true);
        console.log('Using stored location:', storedZone);
      }
    }
  };

  // Get user's current location with comprehensive error handling
  const getCurrentLocation = (silent = false) => {
    return new Promise((resolve, reject) => {
      if (!navigator.geolocation) {
        reject(new Error('Geolocation is not supported by this browser.'));
        return;
      }

      const successCallback = (position) => {
        const location = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          accuracy: position.coords.accuracy,
          timestamp: new Date().toISOString()
        };
        console.log('GPS Location obtained:', location);
        resolve(location);
      };

      const errorCallback = (error) => {
        console.error('Geolocation error:', error);
        
        let errorMessage = 'Unable to detect your location. ';
        switch(error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = 'Location access was denied. Please allow location access in your browser settings to use automatic detection.';
            setLocationPermission('denied');
            if (!silent) setLocationDialog(true);
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = 'Location information is unavailable. Please check your device location settings.';
            break;
          case error.TIMEOUT:
            errorMessage = 'Location request timed out. Please try again.';
            break;
          default:
            errorMessage = 'An unknown error occurred while getting your location.';
        }
        reject(new Error(errorMessage));
      };

      const options = {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000 // 5 minutes cache
      };

      navigator.geolocation.getCurrentPosition(successCallback, errorCallback, options);
    });
  };

  // Show location permission dialog
  const handleLocationPermissionRequest = () => {
    setLocationDialog(true);
  };

  // User agrees to share location
  const handleAllowLocation = async () => {
    setLocationDialog(false);
    await handleDetectLocation(false);
  };

  // User denies location sharing
  const handleDenyLocation = () => {
    setLocationDialog(false);
    setLocationPermission('denied');
    setError('Location access is required for automatic zone detection. Please select your zone manually.');
    localStorage.setItem('locationPermission', 'denied');
  };

  // Detect location and set zone with precise mapping
  const handleDetectLocation = async (silent = false) => {
    setLocationLoading(true);
    setError(null);
    
    try {
      const location = await getCurrentLocation(silent);
      const zoneCode = findClosestZone(location.latitude, location.longitude);
      
      setUserLocation(location);
      setSelectedZone(zoneCode);
      setUsingGPS(true);
      setLocationPermission('granted');
      
      // Save for push notifications and background use
      localStorage.setItem('userPrayerZone', zoneCode);
      localStorage.setItem('userLocation', JSON.stringify(location));
      localStorage.setItem('locationPermission', 'granted');
      localStorage.setItem('lastLocationUpdate', new Date().toISOString());
      
      await loadPrayerTimes(zoneCode);
      
      console.log('Location detection successful:', {
        coordinates: `${location.latitude}, ${location.longitude}`,
        accuracy: `${location.accuracy}m`,
        zone: zoneCode
      });

      if (!silent) {
        setError(`📍 Location detected! Set to zone: ${zoneCode}`);
        setTimeout(() => setError(null), 5000);
      }
      
    } catch (error) {
      console.error('Location detection failed:', error);
      if (!silent) {
        setError(error.message);
      }
    } finally {
      setLocationLoading(false);
    }
  };

  // Fetch prayer times from backend
  const fetchPrayerTimes = async (zoneCode) => {
    try {
      setError(null);
      const response = await fetch(`http://localhost:5000/api/prayertimes/${zoneCode}`);
      
      if (!response.ok) throw new Error('Failed to fetch prayer times');
      
      const data = await response.json();
      if (data.success) {
        return {
          ...data.data,
          method: 'JAKIM e-solat.gov.my (Live)',
          success: true
        };
      } else {
        throw new Error(data.error || 'Failed to get prayer times');
      }
    } catch (error) {
      console.error('API Error:', error);
      throw error;
    }
  };

  const loadPrayerTimes = async (zoneCode = selectedZone) => {
    setLoading(true);
    try {
      const times = await fetchPrayerTimes(zoneCode);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
      setSelectedZone(zoneCode);
    } catch (error) {
      console.error('Error loading prayer times:', error);
      setError('Unable to fetch prayer times. Please check your backend server.');
    } finally {
      setLoading(false);
    }
  };

  const handleZoneChange = async (zoneCode) => {
    setSelectedZone(zoneCode);
    setUsingGPS(false);
    setUserLocation(null);
    localStorage.setItem('userManualZone', zoneCode);
    localStorage.removeItem('userPrayerZone');
    await loadPrayerTimes(zoneCode);
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer (for reminder notifications)
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      if (prayerTime > now) {
        return { 
          name: prayer.name, 
          time: prayer.time, 
          timeObject: prayerTime,
          type: prayer.name.toLowerCase()
        };
      }
    }

    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return { 
      name: 'Fajr', 
      time: prayerTimes.fajr, 
      timeObject: tomorrow, 
      isTomorrow: true,
      type: 'fajr'
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    const [time, modifier] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  // Auto-location prompt for new users
  useEffect(() => {
    const hasManualSelection = localStorage.getItem('userManualZone');
    const hasAutoLocation = localStorage.getItem('userPrayerZone');
    const permissionStatus = localStorage.getItem('locationPermission');
    
    if (!hasManualSelection && !hasAutoLocation && permissionStatus !== 'denied') {
      const timer = setTimeout(() => {
        setLocationDialog(true);
      }, 2000);
      
      return () => clearTimeout(timer);
    }
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: '🌅', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '☀️', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: '🌇', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: '🌆', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: '🌙', type: 'isha' }
  ];

  if (loading && !prayerTimes) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times from e-solat.gov.my...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              🕌 Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
              {usingGPS && ' • Using GPS Location'}
            </Typography>
          </Box>
          
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              size="small"
              startIcon={<MyLocation />}
              onClick={handleLocationPermissionRequest}
              disabled={locationLoading}
              variant={usingGPS ? "contained" : "outlined"}
              color={usingGPS ? "success" : "primary"}
            >
              {locationLoading ? 'Detecting...' : 'My Location'}
            </Button>
            <Button
              size="small"
              startIcon={<Refresh />}
              onClick={() => loadPrayerTimes()}
              disabled={loading}
            >
              Refresh
            </Button>
          </Box>
        </Box>

        {error && (
          <Alert severity={error.includes('denied') ? "warning" : "error"} sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {/* Location Permission Dialog */}
        <Dialog open={locationDialog} onClose={handleDenyLocation}>
          <DialogTitle sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <LocationOn color="primary" />
            Enable Location Access
          </DialogTitle>
          <DialogContent>
            <DialogContentText>
              To provide accurate prayer times and <strong>real-time reminders</strong> for your exact location, 
              we need access to your device's location.
            </DialogContentText>
            <Box sx={{ mt: 2, p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
              <Typography variant="body2" fontWeight="bold">
                Benefits of enabling location:
              </Typography>
              <Typography variant="body2">
                • <strong>Accurate prayer times</strong> for your exact area
              </Typography>
              <Typography variant="body2">
                • <strong>Precise adhan/solat reminders</strong> even when app is closed
              </Typography>
              <Typography variant="body2">
                • Automatic zone detection
              </Typography>
              <Typography variant="body2">
                • Background location updates for travel
              </Typography>
            </Box>
            <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 2 }}>
              Your location is only used to determine prayer times and is stored locally on your device.
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleDenyLocation}>
              No Thanks
            </Button>
            <Button onClick={handleAllowLocation} variant="contained" autoFocus>
              Allow Location
            </Button>
          </DialogActions>
        </Dialog>

        {/* Location Info */}
        {usingGPS && userLocation && (
          <Alert severity="success" sx={{ mb: 2 }}>
            <Typography variant="body2">
              📍 Using your current location 
              {userLocation.accuracy && ` (Accuracy: ${Math.round(userLocation.accuracy)}m)`}
            </Typography>
            <Typography variant="body2">
              Zone automatically set to: <strong>{selectedZone}</strong>
            </Typography>
            <Typography variant="caption" sx={{ display: 'block', mt: 0.5 }}>
              ✅ Push notifications will use your exact location
            </Typography>
          </Alert>
        )}

        {/* Manual Selection Prompt */}
        {locationPermission === 'denied' && !usingGPS && (
          <Alert severity="info" sx={{ mb: 2 }}>
            <Typography variant="body2">
              Please select your zone manually for accurate prayer times and reminders.
            </Typography>
          </Alert>
        )}

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {/* Single Zone Selection */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Zone</InputLabel>
          <Select
            value={selectedZone}
            label="Select Zone"
            onChange={(e) => handleZoneChange(e.target.value)}
            disabled={loading}
          >
            {malaysianZones.map((state) => [
              <Typography 
                key={state.group} 
                component="div" 
                sx={{ 
                  px: 2, 
                  py: 1, 
                  fontWeight: 'bold', 
                  backgroundColor: 'grey.100',
                  borderBottom: '1px solid',
                  borderColor: 'divider'
                }}
              >
                {state.group}
              </Typography>,
              ...state.zones.map(zone => (
                <MenuItem key={zone.value} value={zone.value} sx={{ pl: 3 }}>
                  {zone.label}
                </MenuItem>
              ))
            ])}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              🎯 Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
            {usingGPS && (
              <Typography variant="caption" sx={{ display: 'block', mt: 1, opacity: 0.9 }}>
                ✅ Reminders will use your exact location
              </Typography>
            )}
          </Box>
        )}

        {/* Prayer Times List */}
        {prayerTimes && (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {prayers.map((prayer) => {
              const isCompleted = isPracticeCompleted(prayer.type);
              const isNextPrayer = nextPrayer?.name === prayer.name;
              
              return (
                <Box 
                  key={prayer.name}
                  sx={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    alignItems: 'center',
                    p: 2,
                    borderRadius: 2,
                    bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                    border: isNextPrayer ? '2px solid' : '1px solid',
                    borderColor: isNextPrayer ? 'primary.main' : 'divider',
                    opacity: isCompleted ? 0.8 : 1
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Typography variant="h5">{prayer.icon}</Typography>
                    <Box>
                      <Typography 
                        variant="h6"
                        sx={{ 
                          textDecoration: isCompleted ? 'line-through' : 'none',
                          color: isCompleted ? 'text.secondary' : 'text.primary'
                        }}
                      >
                        {prayer.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {prayer.time || '--:--'}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    {isNextPrayer && (
                      <Chip label="Next" color="primary" size="small" />
                    )}
                    <IconButton
                      onClick={() => handlePracticeToggle(prayer.type)}
                      color={isCompleted ? "success" : "default"}
                      sx={{
                        border: isCompleted ? '2px solid' : '1px solid',
                        borderColor: isCompleted ? 'success.main' : 'grey.400'
                      }}
                    >
                      {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                    </IconButton>
                  </Box>
                </Box>
              );
            })}
          </Box>
        )}

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            {prayerTimes?.method} • Zone: {selectedZone}
            {usingGPS && ' • GPS Active'}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Perfect for prayer reminders • 100% Accurate to e-solat.gov.my
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
