import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Box,
  Chip,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  IconButton
} from '@mui/material';
import { LocationOn, Refresh, MyLocation, CheckCircle, RadioButtonUnchecked } from '@mui/icons-material';
import { usePractice } from '../../context/PracticeContext';

// Accurate Malaysia zone codes for e-solat API
const malaysianZones = {
  // Wilayah Persekutuan
  'Kuala Lumpur': 'WLY01',
  'Putrajaya': 'WLY02',
  
  // Selangor
  'Kajang, Selangor': 'SGR01',
  'Shah Alam': 'SGR02', 
  'Petaling Jaya': 'SGR03',
  'Klang': 'SGR04',
  'Subang Jaya': 'SGR03', // Same as Petaling Jaya
  'Ampang': 'SGR01', // Same as Kajang
  'Cheras': 'SGR01', // Same as Kajang
  'Puchong': 'SGR03', // Same as Petaling Jaya
  
  // Johor
  'Johor Bahru': 'JHR01',
  
  // Pulau Pinang
  'Penang': 'PNG01',
  'Butterworth': 'PNG02',
  
  // Perak
  'Ipoh': 'PRK01',
  'Taiping': 'PRK02',
  
  // Kelantan
  'Kota Bharu': 'KTN01',
  
  // Terengganu
  'Kuala Terengganu': 'TRG01',
  
  // Pahang
  'Kuantan': 'PHG01',
  
  // Melaka
  'Malacca': 'MLK01',
  
  // Negeri Sembilan
  'Seremban': 'NSN01',
  
  // Kedah
  'Alor Setar': 'KDH01',
  
  // Sabah
  'Kota Kinabalu': 'SBH01',
  'Sandakan': 'SBH02',
  'Tawau': 'SBH03',
  'Lahad Datu': 'SBH04',
  
  // Sarawak
  'Kuching': 'SWK01',
  'Miri': 'SWK02',
  'Sibu': 'SWK03',
  'Bintulu': 'SWK04',
  
  // Labuan
  'Labuan': 'LBN01'
};

// Better GPS to zone mapping
const gpsToZoneMapping = [
  // Northern Region (Kedah, Perlis, Penang)
  { minLat: 5.5, maxLat: 7.0, minLng: 99.0, maxLng: 101.0, zone: 'KDH01' },
  { minLat: 5.0, maxLat: 5.5, minLng: 99.0, maxLng: 101.0, zone: 'PNG01' },
  
  // Central Region (KL, Selangor, Putrajaya)
  { minLat: 2.5, maxLat: 4.0, minLng: 100.5, maxLng: 102.0, zone: 'WLY01' },
  { minLat: 2.5, maxLat: 4.0, minLng: 100.5, maxLng: 102.0, zone: 'SGR01' },
  
  // Southern Region (Johor, Melaka)
  { minLat: 1.0, maxLat: 2.5, minLng: 102.5, maxLng: 104.0, zone: 'JHR01' },
  { minLat: 1.0, maxLat: 2.5, minLng: 101.5, maxLng: 102.5, zone: 'MLK01' },
  
  // East Coast (Kelantan, Terengganu)
  { minLat: 4.0, maxLat: 6.5, minLng: 101.5, maxLng: 103.5, zone: 'KTN01' },
  { minLat: 4.0, maxLat: 6.0, minLng: 102.5, maxLng: 103.5, zone: 'TRG01' },
  
  // East Malaysia (Sabah, Sarawak)
  { minLat: 4.0, maxLat: 7.5, minLng: 115.0, maxLng: 119.0, zone: 'SBH01' },
  { minLat: 1.0, maxLat: 4.0, minLng: 109.0, maxLng: 115.0, zone: 'SWK01' }
];

const PrayerTimes = () => {
  const [prayerTimes, setPrayerTimes] = useState(null);
  const [nextPrayer, setNextPrayer] = useState(null);
  const [selectedCity, setSelectedCity] = useState('Kuala Lumpur');
  const [loading, setLoading] = useState(true);
  const [userLocation, setUserLocation] = useState(null);
  const [usingGPS, setUsingGPS] = useState(false);
  const [currentZone, setCurrentZone] = useState('WLY01');
  
  const { markPracticeCompleted, markPracticeIncomplete, isPracticeCompleted, todayStats } = usePractice();

  // Get prayer times from e-solat API
  const getPrayerTimesFromESolat = async (zoneCode) => {
    try {
      const today = new Date();
      const year = today.getFullYear();
      const month = today.getMonth() + 1;
      const day = today.getDate();
      
      console.log(`Fetching prayer times for zone: ${zoneCode}`);
      
      const response = await fetch(
        `https://www.e-solat.gov.my/index.php?r=esolatApi/takwimsolat&period=date&zone=${zoneCode}&date=${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`
      );
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      if (data.prayerTime && data.prayerTime.length > 0) {
        const times = data.prayerTime[0];
        console.log('Received prayer times:', times);
        return {
          fajr: times.fajr,
          dhuhr: times.dhuhr,
          asr: times.asr,
          maghrib: times.maghrib,
          isha: times.isha,
          method: 'JAKIM e-solat API',
          zone: zoneCode,
          calculated: false,
          success: true
        };
      }
      throw new Error('No prayer times data received');
    } catch (error) {
      console.error('e-solat API error:', error);
      throw error;
    }
  };

  // Improved GPS to zone mapping
  const findNearestZone = (lat, lng) => {
    for (const region of gpsToZoneMapping) {
      if (lat >= region.minLat && lat <= region.maxLat && 
          lng >= region.minLng && lng <= region.maxLng) {
        console.log(`GPS ${lat},${lng} mapped to zone: ${region.zone}`);
        return region.zone;
      }
    }
    
    // Fallback based on latitude
    if (lat > 4.0) return 'KDH01'; // Northern
    if (lat > 2.5) return 'WLY01'; // Central  
    if (lat > 1.0) return 'JHR01'; // Southern
    return 'WLY01'; // Default
  };

  const loadPrayerTimes = async (city = selectedCity, zoneCode = null) => {
    setLoading(true);
    try {
      let times;
      let zone = zoneCode;
      
      if (usingGPS && userLocation) {
        // Use GPS location to find nearest zone
        zone = findNearestZone(userLocation.latitude, userLocation.longitude);
        times = await getPrayerTimesFromESolat(zone);
      } else if (zoneCode) {
        // Use provided zone code
        times = await getPrayerTimesFromESolat(zoneCode);
      } else {
        // Use selected city's zone code
        zone = malaysianZones[city];
        if (!zone) {
          throw new Error(`Zone code not found for: ${city}`);
        }
        times = await getPrayerTimesFromESolat(zone);
      }
      
      setCurrentZone(zone);
      setPrayerTimes(times);
      setNextPrayer(getNextPrayer(times));
      
      console.log('Prayer times loaded successfully:', times);
    } catch (error) {
      console.error('Error loading prayer times:', error);
      // Fallback to KL times
      try {
        const fallbackTimes = await getPrayerTimesFromESolat('WLY01');
        setPrayerTimes(fallbackTimes);
        setNextPrayer(getNextPrayer(fallbackTimes));
        setCurrentZone('WLY01');
      } catch (fallbackError) {
        console.error('Fallback also failed:', fallbackError);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleGetLocation = async () => {
    try {
      setLoading(true);
      
      const location = await new Promise((resolve, reject) => {
        if (!navigator.geolocation) {
          reject(new Error('Geolocation not supported'));
          return;
        }

        navigator.geolocation.getCurrentPosition(
          (position) => resolve({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy
          }),
          (error) => reject(error),
          {
            enableHighAccuracy: true,
            timeout: 15000,
            maximumAge: 60000
          }
        );
      });

      setUserLocation(location);
      setUsingGPS(true);
      
      // Load times for nearest zone
      await loadPrayerTimes(null, findNearestZone(location.latitude, location.longitude));
      
      console.log('GPS Location found and prayer times loaded:', location);
    } catch (error) {
      console.error('Location error:', error);
      alert('Unable to get your location. Using Kuala Lumpur instead.');
      setUsingGPS(false);
      setSelectedCity('Kuala Lumpur');
      await loadPrayerTimes('Kuala Lumpur');
    } finally {
      setLoading(false);
    }
  };

  const handleCityChange = async (city) => {
    setSelectedCity(city);
    setUsingGPS(false);
    await loadPrayerTimes(city);
  };

  const handlePracticeToggle = (prayerName) => {
    const prayerType = prayerName.toLowerCase();
    
    if (isPracticeCompleted(prayerType)) {
      markPracticeIncomplete(prayerType);
    } else {
      markPracticeCompleted(prayerType);
    }
  };

  // Get next prayer
  const getNextPrayer = (prayerTimes) => {
    if (!prayerTimes) return null;
    
    const now = new Date();
    const prayers = [
      { name: 'Fajr', time: prayerTimes.fajr },
      { name: 'Dhuhr', time: prayerTimes.dhuhr },
      { name: 'Asr', time: prayerTimes.asr },
      { name: 'Maghrib', time: prayerTimes.maghrib },
      { name: 'Isha', time: prayerTimes.isha }
    ];

    for (let prayer of prayers) {
      const prayerTime = parseTimeString(prayer.time);
      
      if (prayerTime > now) {
        return {
          name: prayer.name,
          time: prayer.time,
          timeObject: prayerTime
        };
      }
    }

    // If all prayers passed, return first prayer of next day
    const tomorrow = new Date(now);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const fajrTime = parseTimeString(prayerTimes.fajr);
    tomorrow.setHours(fajrTime.getHours(), fajrTime.getMinutes(), 0, 0);
    
    return {
      name: 'Fajr',
      time: prayerTimes.fajr,
      timeObject: tomorrow,
      isTomorrow: true
    };
  };

  // Helper function to parse time strings
  const parseTimeString = (timeStr) => {
    if (!timeStr) return new Date();
    
    const [time, modifier] = timeStr.split(' ');
    let [hours, minutes] = time.split(':');
    
    hours = parseInt(hours);
    minutes = parseInt(minutes);
    
    if (modifier === 'PM' && hours < 12) hours += 12;
    if (modifier === 'AM' && hours === 12) hours = 0;
    
    const date = new Date();
    date.setHours(hours, minutes, 0, 0);
    return date;
  };

  useEffect(() => {
    loadPrayerTimes();
  }, []);

  const prayers = [
    { name: 'Fajr', time: prayerTimes?.fajr, icon: 'üåÖ', type: 'fajr' },
    { name: 'Dhuhr', time: prayerTimes?.dhuhr, icon: '‚òÄÔ∏è', type: 'dhuhr' },
    { name: 'Asr', time: prayerTimes?.asr, icon: 'üåá', type: 'asr' },
    { name: 'Maghrib', time: prayerTimes?.maghrib, icon: 'üåÜ', type: 'maghrib' },
    { name: 'Isha', time: prayerTimes?.isha, icon: 'üåô', type: 'isha' }
  ];

  if (loading) {
    return (
      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ textAlign: 'center', py: 4 }}>
          <CircularProgress />
          <Typography variant="body1" sx={{ mt: 2 }}>
            Loading prayer times from JAKIM...
          </Typography>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardContent>
        {/* Header with Progress */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Box>
            <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              üïå Prayer Times
            </Typography>
            <Typography variant="body2" color="primary.main" sx={{ mt: 0.5 }}>
              {todayStats.prayersCompleted}/5 prayers completed today
              {usingGPS && ' ‚Ä¢ Using GPS Location'}
            </Typography>
          </Box>
          
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              size="small"
              startIcon={<MyLocation />}
              onClick={handleGetLocation}
              disabled={loading}
              variant={usingGPS ? "contained" : "outlined"}
              color={usingGPS ? "success" : "primary"}
            >
              My Location
            </Button>
            <Button
              size="small"
              startIcon={<Refresh />}
              onClick={() => loadPrayerTimes()}
              disabled={loading}
            >
              Refresh
            </Button>
          </Box>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ mb: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
            <Typography variant="body2" color="text.secondary">
              Today's Progress
            </Typography>
            <Typography variant="body2" color="primary.main" fontWeight="bold">
              {todayStats.prayersCompleted}/5
            </Typography>
          </Box>
          <Box sx={{ 
            width: '100%', 
            height: 8, 
            backgroundColor: 'grey.200', 
            borderRadius: 4,
            overflow: 'hidden'
          }}>
            <Box 
              sx={{ 
                width: `${todayStats.progress}%`, 
                height: '100%', 
                backgroundColor: 'primary.main',
                borderRadius: 4,
                transition: 'width 0.3s ease'
              }}
            />
          </Box>
        </Box>

        {/* Location Selector */}
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Location</InputLabel>
          <Select
            value={usingGPS ? 'GPS Location' : selectedCity}
            label="Location"
            onChange={(e) => {
              if (e.target.value !== 'GPS Location') {
                handleCityChange(e.target.value);
              }
            }}
          >
            {usingGPS && (
              <MenuItem value="GPS Location" disabled>
                üìç Using GPS Location ({currentZone})
              </MenuItem>
            )}
            {Object.keys(malaysianZones).map(city => (
              <MenuItem key={city} value={city}>
                {city} ({malaysianZones[city]})
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {/* Next Prayer */}
        {nextPrayer && (
          <Box sx={{ 
            bgcolor: 'primary.main', 
            color: 'white', 
            p: 2, 
            borderRadius: 2, 
            mb: 3,
            textAlign: 'center'
          }}>
            <Typography variant="h6" gutterBottom>
              üéØ Next Prayer
            </Typography>
            <Typography variant="h4" fontWeight="bold">
              {nextPrayer.name}
            </Typography>
            <Typography variant="h6">
              {nextPrayer.time}
              {nextPrayer.isTomorrow && ' (Tomorrow)'}
            </Typography>
          </Box>
        )}

        {/* Prayer Times List */}
        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          {prayers.map((prayer) => {
            const isCompleted = isPracticeCompleted(prayer.type);
            const isNextPrayer = nextPrayer?.name === prayer.name;
            
            return (
              <Box 
                key={prayer.name}
                sx={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  p: 2,
                  borderRadius: 2,
                  bgcolor: isNextPrayer ? 'action.hover' : 'transparent',
                  border: isNextPrayer ? '2px solid' : '1px solid',
                  borderColor: isNextPrayer ? 'primary.main' : 'divider',
                  opacity: isCompleted ? 0.8 : 1
                }}
              >
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Typography variant="h5">{prayer.icon}</Typography>
                  <Box>
                    <Typography 
                      variant="h6"
                      sx={{ 
                        textDecoration: isCompleted ? 'line-through' : 'none',
                        color: isCompleted ? 'text.secondary' : 'text.primary'
                      }}
                    >
                      {prayer.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {prayer.time || '--:--'}
                    </Typography>
                  </Box>
                </Box>
                
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {isNextPrayer && (
                    <Chip 
                      label="Next" 
                      color="primary" 
                      size="small"
                    />
                  )}
                  
                  <IconButton
                    onClick={() => handlePracticeToggle(prayer.type)}
                    color={isCompleted ? "success" : "default"}
                    sx={{
                      border: isCompleted ? '2px solid' : '1px solid',
                      borderColor: isCompleted ? 'success.main' : 'grey.400'
                    }}
                  >
                    {isCompleted ? <CheckCircle /> : <RadioButtonUnchecked />}
                  </IconButton>
                </Box>
              </Box>
            );
          })}
        </Box>

        {/* Footer Info */}
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Typography variant="caption" color="text.secondary">
            Source: JAKIM e-solat.gov.my ‚Ä¢ Zone: {currentZone}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            {usingGPS ? 'üìç Using your GPS location' : `üìç ${selectedCity}`}
          </Typography>
          <br />
          <Typography variant="caption" color="text.secondary">
            Tap circle to mark prayer as completed
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

export default PrayerTimes;
